import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  features = [
    {
      title: 'Receipt Capture',
      description: 'Capture and store receipts easily with our app.',
      image: 'https://example.com/feature1.jpg'
    },
    {
      title: 'Location-based Tracking',
      description: 'Automatically track expenses based on your location.',
      image: 'https://example.com/feature2.jpg'
    },
    {
      title: 'Expense Categorization',
      description: 'Categorize your expenses for better management.',
      image: 'https://example.com/feature3.jpg'
    },
    {
      title: 'Multi-currency Support',
      description: 'Manage expenses in multiple currencies seamlessly.',
      image: 'https://example.com/feature4.jpg'
    },
    {
      title: 'Budget Management',
      description: 'Set budgets and track your spending effectively.',
      image: 'https://example.com/feature5.jpg'
    },
    {
      title: 'Expense Analysis',
      description: 'Analyze your expenses with detailed reports.',
      image: 'https://example.com/feature6.jpg'
    }
  ];
}
